				\usepackage{wasysym} 
				\usepackage{amsfonts} 
				\usepackage{amssymb} 
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\:{E}_{TX}\left(N,d\right)=\left\{\begin{array}{c}N\times\:\left({E}_{elec}+{E}_{amp}.{d}^{2}\right)\:\:\:\:\:\:\:if\:d\le\:\sqrt{\frac{{E}_{fs}}{{E}_{amp}}}\\\:N\times\:\left({E}_{elec}+{E}_{amp}.{d}^{4}\right)\:\:\:\:\:\:\:\:\:\:\:\:otherwise\end{array}\right.$$\end{document}

